import React, { Component } from 'react'
import axios from 'axios'
import Layout from '../../components/Layout'

export default class detail extends Component {

    static async getInitialProps({ query }) {
        const res = await axios.get('http://localhost:3001/blogs/' + query.id )
        return { blog : res.data }
    }

    render() {
        const { blog } = this.props
        return (
            <Layout>
                <div className="container"> 
                    <h1 className="mt-4 mb-4 title">{blog.subject}</h1>
                    <div className="row">
                        <div className="col-6">
                            <img className="img-fluid" src={blog.thumbnail} />
                        </div>
                        <div className="col-6">
                            <div className="text-justify detail">
                                {blog.detail}
                            </div>
                            <hr />
                            <h5 className="text-success">
                                เขียนโดย {blog.created_by.user.firtname} {blog.created_by.user.lastname}
                            </h5>
                            <h5 className="text-muted">
                                แก้เมื่อ {blog.update_date}
                            </h5>
                        </div>
                    </div>

                </div>
            </Layout>
        )
    }
}
